import React, { useState, useRef, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Physics, RigidBody, CuboidCollider } from '@react-three/rapier'
import { OrbitControls, Environment, Sky, Grid, useVideoTexture } from '@react-three/drei'
import { GoogleGenerativeAI } from "@google/generative-ai"

// --- CONFIGURATION ---
const API_KEY = "AIzaSyCYUzL_y8r-AUuSPiSbBahgmxclZd3pqNQ" 
const genAI = new GoogleGenerativeAI(API_KEY)

const PHYSICS_PROPS = {
  bookshelf: { size: [1.2, 4, 0.6], mass: 25, color: '#5d4037', hazardMsg: "Tipping Risk: High. Anchor to wall studs." },
  refrigerator: { size: [1.6, 3, 1.6], mass: 150, color: '#bdc3c7', hazardMsg: "Crush Hazard: High. Secure door latches." },
  chair: { size: [0.8, 1, 0.8], mass: 8, color: '#2c3e50', hazardMsg: "Sliding Hazard: Low. Use floor grips." },
  table: { size: [3, 1, 2], mass: 45, color: '#8B4513', hazardMsg: "Stable base. Safe to duck under." },
  lamp: { size: [0.4, 3.5, 0.4], mass: 5, color: '#f1c40f', hazardMsg: "Falling Hazard: High." },
  tv: { size: [2.5, 1.5, 0.2], mass: 12, color: '#222', hazardMsg: "Impact Hazard: Use mounting brackets." },
  plant: { size: [0.7, 1.5, 0.7], mass: 10, color: '#27ae60', hazardMsg: "Spillage Hazard: Use heavy pots." }
}

function VideoPlane({ url }) {
  const texture = useVideoTexture(url)
  return (
    <mesh position={[0, 5, -12]} scale={[32, 18, 1]}>
      <planeGeometry />
      <meshBasicMaterial map={texture} transparent opacity={0.5} />
    </mesh>
  )
}

function Room({ magnitude }) {
  const rigidBody = useRef()
  useFrame(({ clock }) => {
    if (rigidBody.current && magnitude > 0) {
      const t = clock.getElapsedTime()
      const freq = 12 + magnitude * 2
      const amp = Math.pow(magnitude / 9, 3) * 0.2
      rigidBody.current.setNextKinematicTranslation({ 
        x: Math.sin(t * freq) * amp, y: 0, z: Math.cos(t * freq * 1.1) * amp 
      })
    }
  })
  return (
    <RigidBody ref={rigidBody} type="kinematicPosition" colliders={false}>
      <CuboidCollider args={[25, 0.1, 25]} friction={4.0} />
      <Grid infiniteGrid sectionColor="#333" cellColor="#111" />
    </RigidBody>
  )
}

export default function App() {
  const [magnitude, setMagnitude] = useState(0)
  const [videoUrl, setVideoUrl] = useState(null)
  const [detectedObjects, setDetectedObjects] = useState([])
  const [loading, setLoading] = useState(false)

  // MOCK DATA: If the network fails, use this so your demo still works!
  const loadMockData = () => {
    setDetectedObjects([
      { type: 'refrigerator', x: -5, z: -8 },
      { type: 'bookshelf', x: 4, z: -6 },
      { type: 'table', x: 0, z: -4 },
      { type: 'chair', x: 2, z: -2 }
    ])
  }

  const toBase64 = (file) => new Promise((resolve, reject) => {
    const reader = new FileReader()
    reader.readAsDataURL(file)
    reader.onload = () => resolve(reader.result.split(',')[1])
    reader.onerror = (error) => reject(error)
  })

  async function analyzeVideo(file) {
    setLoading(true)
    try {
      // Use 2.0 Flash - it's much faster for video tasks
      const model = genAI.getGenerativeModel({ 
        model: "gemini-2.0-flash",
        generationConfig: { responseMimeType: "application/json" } 
      })
      const base64Data = await toBase64(file)

      const prompt = `Return JSON array: [{"type": "bookshelf"|"refrigerator"|"chair"|"table"|"lamp"|"tv"|"plant", "x": number, "z": number}]. x: -10 to 10, z: -10 to 0.`

      const result = await model.generateContent([
        { inlineData: { data: base64Data, mimeType: file.type } },
        prompt
      ])

      setDetectedObjects(JSON.parse(result.response.text()))
    } catch (error) {
      console.error("AI Error (Network likely):", error)
      loadMockData() // Fallback to mock data on error
    } finally {
      setLoading(false)
    }
  }

  return (
    <div style={{ width: '100vw', height: '100vh', background: '#000', color: 'white' }}>
      <div style={{ position: 'absolute', zIndex: 10, padding: '20px', background: 'rgba(0,0,0,0.8)', width: '350px', height: '100vh' }}>
        <h2 style={{ color: '#00ffcc' }}>QUAKEPROOF AI</h2>
        <input type="file" accept="video/*" onChange={(e) => {
          const file = e.target.files[0]
          if (file) { setVideoUrl(URL.createObjectURL(file)); analyzeVideo(file); }
        }} />

        {loading && <p style={{color: '#ffa500'}}>Analysing video frames...</p>}

        {detectedObjects.length > 0 && (
          <div style={{marginTop: '20px'}}>
            <label>Magnitude: {magnitude}</label>
            <input type="range" min="0" max="9" step="0.1" style={{width: '100%'}} value={magnitude} onChange={(e) => setMagnitude(parseFloat(e.target.value))} />
            <button onClick={loadMockData} style={{marginTop: '10px', fontSize: '10px'}}>Force Mock (Demo Backup)</button>
            <div style={{marginTop: '20px', fontSize: '12px'}}>
              {detectedObjects.map((obj, i) => (
                <div key={i} style={{borderLeft: '2px solid #00ffcc', paddingLeft: '10px', marginBottom: '10px'}}>
                  <b style={{textTransform: 'uppercase'}}>{obj.type}</b>: {PHYSICS_PROPS[obj.type]?.hazardMsg}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <Canvas camera={{ position: [0, 15, 25], fov: 35 }}>
        <Suspense fallback={null}>
          {videoUrl && <VideoPlane url={videoUrl} />}
          <Physics gravity={[0, -9.81, 0]}>
            <Room magnitude={magnitude} />
            {detectedObjects.map((obj, i) => {
              const data = PHYSICS_PROPS[obj.type] || PHYSICS_PROPS.chair
              return (
                <RigidBody key={i} position={[obj.x, 5, obj.z]} mass={data.mass}>
                  <mesh>
                    <boxGeometry args={data.size} />
                    <meshStandardMaterial color={data.color} />
                  </mesh>
                </RigidBody>
              )
            })}
          </Physics>
        </Suspense>
        <OrbitControls />
        <Environment preset="city" />
      </Canvas>
    </div>
  )
}
