import React, { useState, useRef, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Physics, RigidBody, CuboidCollider } from '@react-three/rapier'
import { OrbitControls, Environment, Sky, Grid, useTexture } from '@react-three/drei'

// 1. DATA LOOKUP: The "Brain" that knows how heavy objects are
const OBJECT_LIBRARY = {
  fridge: { size: [1.6, 3, 1.6], mass: 150, color: '#bdc3c7', label: 'Refrigerator' },
  bookshelf: { size: [1.2, 4, 0.6], mass: 25, color: '#5d4037', label: 'Tall Bookshelf' },
  chair: { size: [0.8, 1, 0.8], mass: 8, color: '#2c3e50', label: 'Dining Chair' },
  tv_stand: { size: [2, 1, 1], mass: 30, color: '#333', label: 'TV Stand' }
}

// 2. THE BACKGROUND PHOTO
function PhotoPlane({ url }) {
  const texture = useTexture(url)
  return (
    <mesh position={[0, 5, -8]} scale={[24, 12, 1]}>
      <planeGeometry />
      <meshBasicMaterial map={texture} transparent opacity={0.7} />
    </mesh>
  )
}

// 3. THE PHYSICS ROOM
function Room({ magnitude }) {
  const rigidBody = useRef()
  useFrame(({ clock }) => {
    if (rigidBody.current && magnitude > 0) {
      const t = clock.getElapsedTime()
      const intensity = Math.pow(magnitude / 9, 3)
      const freq = 10 + magnitude * 2
      const amp = intensity * 0.2
      rigidBody.current.setNextKinematicTranslation({ 
        x: Math.sin(t * freq) * amp, y: 0, z: Math.cos(t * freq * 1.1) * amp 
      })
    }
  })

  return (
    <RigidBody ref={rigidBody} type="kinematicPosition" colliders={false}>
      <CuboidCollider args={[10, 0.1, 10]} friction={4.0} restitution={0} />
      <Grid infiniteGrid fadeDistance={30} sectionColor="#333" cellColor="#111" />
    </RigidBody>
  )
}

export default function App() {
  const [magnitude, setMagnitude] = useState(0)
  const [image, setImage] = useState(null)
  const [detectedObjects, setDetectedObjects] = useState([])

  // SIMULATED AI VISION: In a real app, this data comes from an API like Gemini Vision
  const runAIDetection = () => {
    const mockDetections = [
      { type: 'bookshelf', pos: [-2, 2.1, -2] },
      { type: 'fridge', pos: [3, 1.5, 1] },
      { type: 'chair', pos: [1, 0.5, 4] }
    ]
    setDetectedObjects(mockDetections)
  }

  const handleUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(URL.createObjectURL(file))
      // Trigger "Vision" after a short delay
      setTimeout(runAIDetection, 1000)
    }
  }

  return (
    <div style={{ width: '100vw', height: '100vh', background: '#000' }}>
      <div style={{ position: 'absolute', zIndex: 10, padding: '25px', color: 'white', background: 'rgba(0,0,0,0.8)', width: '320px', borderRadius: '0 0 15px 0' }}>
        <h2>QuakeProof AI</h2>
        <p style={{fontSize: '13px'}}>1. Upload room photo</p>
        <input type="file" accept="image/*" onChange={handleUpload} />

        {detectedObjects.length > 0 && (
          <div style={{marginTop: '20px'}}>
            <p style={{fontSize: '13px', color: '#00ff88'}}>âœ“ AI Detected {detectedObjects.length} Hazards</p>
            <label>Adjust Magnitude: {magnitude}</label>
            <input type="range" min="0" max="9" step="0.5" style={{width: '100%'}} value={magnitude} onChange={(e) => setMagnitude(parseFloat(e.target.value))} />
            <button onClick={() => setMagnitude(0)} style={{marginTop: '10px', width: '100%'}}>Reset Scene</button>
          </div>
        )}
      </div>

      <Canvas shadows camera={{ position: [0, 6, 14], fov: 40 }}>
        <Sky sunPosition={[100, 20, 100]} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} castShadow intensity={1.5} />

        <Suspense fallback={null}>
          {image && <PhotoPlane url={image} />}

          <Physics key={magnitude} gravity={[0, -9.81, 0]}>
            <Room magnitude={magnitude} />

            {detectedObjects.map((obj, i) => {
              const data = OBJECT_LIBRARY[obj.type]
              return (
                <RigidBody key={i} position={obj.pos} colliders="cuboid" mass={data.mass} restitution={0} friction={2}>
                  <mesh castShadow>
                    <boxGeometry args={data.size} />
                    <meshStandardMaterial color={data.color} transparent opacity={0.8} />
                  </mesh>
                </RigidBody>
              )
            })}
          </Physics>
        </Suspense>

        <OrbitControls makeDefault maxPolarAngle={Math.PI / 2.1} />
        <Environment preset="city" />
      </Canvas>
    </div>
  )
}
