import React, { useState, useRef, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Physics, RigidBody, CuboidCollider } from '@react-three/rapier'
import { OrbitControls, Environment, Sky, Grid, Splat } from '@react-three/drei'
import { GoogleGenerativeAI } from "@google/generative-ai"
import { Buffer } from 'buffer'

// Fix for Rapier + Webpack 5
window.Buffer = window.Buffer || Buffer

const PHYSICS_PROPS = {
  bookshelf: { size: [1.2, 4, 0.6], mass: 25, color: '#5d4037', hazardMsg: "High Tipping Risk" },
  refrigerator: { size: [1.6, 3, 1.6], mass: 150, color: '#bdc3c7', hazardMsg: "Crush Hazard" },
  chair: { size: [0.8, 1, 0.8], mass: 10, color: '#2c3e50', hazardMsg: "Sliding Hazard" },
  table: { size: [3, 1, 2], mass: 45, color: '#8B4513', hazardMsg: "Stable" }
}

function Room({ magnitude }) {
  const rigidBody = useRef()
  useFrame(({ clock }) => {
    if (rigidBody.current && magnitude > 0) {
      const t = clock.getElapsedTime()
      const freq = 12 + magnitude * 2
      const amp = Math.pow(magnitude / 9, 3) * 0.2
      rigidBody.current.setNextKinematicTranslation({ 
        x: Math.sin(t * freq) * amp, 
        y: 0, 
        z: Math.cos(t * freq * 1.1) * amp 
      })
    }
  })
  return (
    <RigidBody ref={rigidBody} type="kinematicPosition" colliders={false}>
      <CuboidCollider args={[20, 0.1, 20]} friction={4} />
      {/* Visible Floor so we don't have a black screen */}
      <mesh rotation={[-Math.PI / 2, 0, 0]} receiveShadow>
        <planeGeometry args={[40, 40]} />
        <meshStandardMaterial color="#222" />
      </mesh>
      <Grid infiniteGrid sectionColor="#00ffcc" cellColor="#111" sectionSize={1} fadeDistance={30} />
    </RigidBody>
  )
}

export default function App() {
  const [magnitude, setMagnitude] = useState(0)
  const [splatUrl, setSplatUrl] = useState(null)
  const [detectedObjects, setDetectedObjects] = useState([])
  const [loading, setLoading] = useState(false)

  const handleVideoUpload = async (e) => {
    setLoading(true)
    // Simulate AI Processing
    setTimeout(() => {
      setSplatUrl("https://huggingface.co/datasets/dylanebert/3dgs/resolve/main/kitchen/kitchen.splat")
      setDetectedObjects([
        { type: 'refrigerator', x: 2, z: -3 },
        { type: 'table', x: -2, z: -1 },
        { type: 'chair', x: 0, z: 2 },
        { type: 'bookshelf', x: -4, z: -5 }
      ])
      setLoading(false)
    }, 2000)
  }

  return (
    <div style={{ width: '100vw', height: '100vh', background: '#000', fontFamily: 'monospace' }}>
      {/* UI Panels */}
      <div style={{ position: 'absolute', zIndex: 10, padding: '25px', color: 'white', background: 'rgba(0,0,0,0.85)', width: '320px', height: '100vh', borderRight: '1px solid #00ffcc' }}>
        <h2 style={{ color: '#00ffcc', margin: 0 }}>QUAKEPROOF v3</h2>
        <p style={{ fontSize: '10px', color: '#888' }}>NEURAL SEISMIC SIMULATOR</p>

        <div style={{ margin: '20px 0', padding: '15px', border: '1px dashed #444', textAlign: 'center' }}>
          <input type="file" accept="video/*" onChange={handleVideoUpload} style={{ width: '100%' }} />
        </div>

        {loading && <p style={{ color: '#00ffcc', animation: 'pulse 1s infinite' }}>ðŸ“¡ RECONSTRUCTING SCENE...</p>}

        {detectedObjects.length > 0 && (
          <div>
            <div style={{ background: '#111', padding: '15px', border: '1px solid #333' }}>
              <label>MAGNITUDE: <b style={{ color: '#00ffcc' }}>{magnitude}</b></label>
              <input type="range" min="0" max="9" step="0.1" style={{ width: '100%', accentColor: '#00ffcc' }} value={magnitude} onChange={(e) => setMagnitude(parseFloat(e.target.value))} />
            </div>

            <h4 style={{ color: '#ff4444', marginTop: '20px', fontSize: '12px' }}>HAZARD LOG:</h4>
            {detectedObjects.map((obj, i) => (
              <div key={i} style={{ fontSize: '10px', padding: '8px', borderBottom: '1px solid #222' }}>
                <span style={{ color: '#00ffcc' }}>{obj.type.toUpperCase()}</span>: {PHYSICS_PROPS[obj.type].hazardMsg}
              </div>
            ))}
          </div>
        )}
      </div>

      <Canvas shadows camera={{ position: [12, 12, 12], fov: 35 }}>
        {/* LIGHTING - Crucial for non-black screen */}
        <ambientLight intensity={0.7} />
        <pointLight position={[10, 10, 10]} intensity={1.5} castShadow />
        <Sky sunPosition={[100, 20, 100]} />

        <Suspense fallback={null}>
          {/* Visual Splat */}
          {splatUrl && (
            <Splat 
              src={splatUrl} 
              position={[0, 1.2, 0]} 
              scale={1.5} 
              rotation={[0, Math.PI, 0]} 
            />
          )}

          {/* Physics Simulation */}
          <Physics gravity={[0, -9.81, 0]}>
            <Room magnitude={magnitude} />

            {detectedObjects.map((obj, i) => {
              const data = PHYSICS_PROPS[obj.type]
              return (
                <RigidBody key={i} position={[obj.x, 4, obj.z]} colliders="cuboid" mass={data.mass}>
                  <mesh castShadow>
                    <boxGeometry args={data.size} />
                    <meshStandardMaterial color={data.color} metalness={0.6} roughness={0.2} transparent opacity={0.8} />
                  </mesh>
                </RigidBody>
              )
            })}
          </Physics>
        </Suspense>

        <OrbitControls makeDefault />
        <Environment preset="city" />
      </Canvas>
    </div>
  )
}
