import React, { useState, useRef, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Physics, RigidBody, CuboidCollider } from '@react-three/rapier'
import { OrbitControls, Environment, Sky, Grid, useTexture } from '@react-three/drei'
import { GoogleGenerativeAI } from "@google/generative-ai"

// --- CONFIGURATION ---
const API_KEY = "AIzaSyCYUzL_y8r-AUuSPiSbBahgmxclZd3pqNQ"
const genAI = new GoogleGenerativeAI(API_KEY)

const PHYSICS_PROPS = {
  bookshelf: { size: [1.2, 4, 0.6], mass: 25, color: '#5d4037', hazardMsg: "Tipping Risk: High" },
  refrigerator: { size: [1.6, 3, 1.6], mass: 150, color: '#bdc3c7', hazardMsg: "Crush Hazard: High" },
  chair: { size: [0.8, 1, 0.8], mass: 8, color: '#2c3e50', hazardMsg: "Sliding Hazard: Low" },
  table: { size: [3, 1, 2], mass: 40, color: '#8B4513', hazardMsg: "Structural: Stable" }
}

// --- COMPONENTS ---
function PhotoPlane({ url }) {
  const texture = useTexture(url)
  return (
    <mesh position={[0, 5, -10]} scale={[28, 14, 1]}>
      <planeGeometry />
      <meshBasicMaterial map={texture} transparent opacity={0.5} />
    </mesh>
  )
}

function Room({ magnitude }) {
  const rigidBody = useRef()
  useFrame(({ clock }) => {
    if (rigidBody.current && magnitude > 0) {
      const t = clock.getElapsedTime()
      const intensity = Math.pow(magnitude / 9, 3)
      const freq = 10 + magnitude * 2
      const amp = intensity * 0.2
      rigidBody.current.setNextKinematicTranslation({ 
        x: Math.sin(t * freq) * amp, y: 0, z: Math.cos(t * freq * 1.1) * amp 
      })
    }
  })
  return (
    <RigidBody ref={rigidBody} type="kinematicPosition" colliders={false}>
      <CuboidCollider args={[20, 0.1, 20]} friction={4.0} restitution={0} />
      <Grid infiniteGrid fadeDistance={40} sectionColor="#333" cellColor="#111" />
    </RigidBody>
  )
}

export default function App() {
  const [magnitude, setMagnitude] = useState(0)
  const [image, setImage] = useState(null)
  const [detectedObjects, setDetectedObjects] = useState([])
  const [loading, setLoading] = useState(false)

  // THE AI LOGIC: Converts Photo to JSON
  async function analyzeImage(file) {
    setLoading(true)
    try {
      const model = genAI.getGenerativeModel({ model: "gemini-2.5-flash" })

      // Convert file to base64 for Gemini
      const reader = new FileReader()
      reader.readAsDataURL(file)
      reader.onload = async () => {
        const base64Data = reader.result.split(',')[1]

        const prompt = `Look at this room. Identify major furniture (bookshelf, refrigerator, chair, table). 
        For each, return a JSON array: [{"type": "bookshelf", "x": -2, "z": -4}]. 
        Keep x between -8 and 8, and z between -8 and -2. Return ONLY the JSON.`

        const result = await model.generateContent([
          prompt,
          { inlineData: { data: base64Data, mimeType: file.type } }
        ])

        const text = result.response.text()
        const cleanedText = text.replace(/```json/g, "").replace(/```/g, "").trim()
        setDetectedObjects(JSON.parse(cleanedText))
        setLoading(false)
      }
    } catch (error) {
      console.error("AI Error:", error)
      setLoading(false)
    }
  }

  const handleUpload = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(URL.createObjectURL(file))
      analyzeImage(file)
    }
  }

  return (
    <div style={{ width: '100vw', height: '100vh', background: '#000' }}>
      <div style={{ position: 'absolute', zIndex: 10, padding: '25px', color: 'white', background: 'rgba(0,0,0,0.9)', width: '350px' }}>
        <h2 style={{ color: '#00ffcc' }}>QUAKEPROOF AI</h2>
        <input type="file" accept="image/*" onChange={handleUpload} />

        {loading && <p>Gemini is scanning your room...</p>}

        {detectedObjects.length > 0 && (
          <div style={{ marginTop: '20px' }}>
            <label>Magnitude: {magnitude}</label>
            <input type="range" min="0" max="9" step="0.5" style={{ width: '100%' }} value={magnitude} onChange={(e) => setMagnitude(parseFloat(e.target.value))} />
            <div style={{ marginTop: '10px' }}>
              {detectedObjects.map((obj, i) => (
                <div key={i} style={{ fontSize: '11px', background: '#222', padding: '5px', margin: '2px' }}>
                  ⚠️ {obj.type.toUpperCase()}: {PHYSICS_PROPS[obj.type]?.hazardMsg}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <Canvas shadows camera={{ position: [0, 8, 15], fov: 35 }}>
        <Sky sunPosition={[100, 20, 100]} />
        <ambientLight intensity={0.5} />
        <Suspense fallback={null}>
          {image && <PhotoPlane url={image} />}
          <Physics key={`${magnitude}-${detectedObjects.length}`} gravity={[0, -9.81, 0]}>
            <Room magnitude={magnitude} />
            {detectedObjects.map((obj, i) => {
              const data = PHYSICS_PROPS[obj.type] || PHYSICS_PROPS.chair
              return (
                <RigidBody key={i} position={[obj.x, 2, obj.z]} colliders="cuboid" mass={data.mass}>
                  <mesh castShadow>
                    <boxGeometry args={data.size} />
                    <meshStandardMaterial color={data.color} transparent opacity={0.7} />
                  </mesh>
                </RigidBody>
              )
            })}
          </Physics>
        </Suspense>
        <OrbitControls />
        <Environment preset="city" />
      </Canvas>
    </div>
  )
}
